INSERT INTO `lesson` (`id`, `label`, `code`, `duration`, `fees`, `type`, `syllabus`, `state`, `top`, `prerequisite`, `path_img`, `date_adding`, `id_user`) VALUES
(1, 'Secrétariat bureautique', 'SEBU', 50, 55000, 'filière', '<p>Word, Excel (intro), power Point&nbsp;</p><p><strong>Pr&eacute;requis:</strong> Concepts th&eacute;oriques&nbsp;en Informatique, composants d&rsquo;un&nbsp;ordinateur, manipulation des&nbsp;fichiers,</p>', 1, 1, '', '', '2018-07-18 18:36:17', 24),
(2, 'Comptabilité Informatisée et de Gestion', 'CIGE', 60, 65000, 'filière', '<p>Introduction &agrave; l&#39;informatique,</p><p>Excel Avanc&eacute;,</p><p>Application de la Gestion Commerciale avec SAGE&nbsp;SAARI&nbsp;<br /><strong>Pr&eacute;requis:</strong> Concepts th&eacute;oriques&nbsp;en Informatique, composants d&rsquo;un&nbsp;ordinateur, manipulation des&nbsp;fichiers, Excel, &nbsp;&hellip;&nbsp;</p>', 1, 1, '', '', '2018-07-18 18:36:17', 65),
(3, 'Réseaux informatiques et Cablâge', 'RICA', 60, 85000, 'filière', '<p><em>Installation, configuration et Cabl&acirc;ge d&rsquo;un r&eacute;seau&nbsp;informatique; </em></p><p><strong><em>Pr&eacute;requis</em></strong><em>: Concepts th&eacute;oriques en Informatique, composants d&rsquo;un ordinateur, manipulation des fichiers, &hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 77),
(4, 'MAINTENANCE INFORMATIQUE', 'MINF', 60, 75000, 'filière', '<p><em>Concepts th&eacute;oriques, composants de l&rsquo;ordinateur, pr&eacute;vention et r&eacute;paration des pannes d&rsquo;ordinateurs, installation logiciels et syst&egrave;mes d&rsquo;exploitation,&hellip;</em></p><p><strong><em>Pr&eacute;requis</em></strong><em>: Concepts th&eacute;oriques en Informatique, composants d&rsquo;un ordinateur, manipulation des fichiers, &hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(5, 'GRAPHISME DE PRODUCTION', 'GRAP', 45, 85000, 'filière', '<p><em>Utilisation des logiciels de graphisme pour produire des cartes de visite, brochure, flyer, &hellip;</em></p><p><strong><em>Pr&eacute;requis</em></strong><em>: Concepts th&eacute;oriques en Informatique, composants d&rsquo;un ordinateur, manipulation des fichiers, &hellip;</em></p>', 1, 3, '', '', '2018-07-18 18:36:17', 0),
(6, 'DéVELOPPEMENT D’APPLICATION WEB', 'DWEB', 50, 60000, 'filière', '<p><em>Conception et r&eacute;alisation&nbsp; d&rsquo;applications web.</em></p><p><strong><em>Pr&eacute;requis</em></strong><em>&nbsp;: vocabulaire de d&eacute;veloppement web (page web, serveur, site web, url,&hellip;) </em></p><p>Que ce soit pour apprendre depuis les bases ou d&#39;utiliser un outil de programmation web, cette formation vous garantie des apptitudes &agrave; <strong>concevoir</strong>, <strong>impl&eacute;menter </strong>ou <strong>coder</strong>, <strong>tester </strong>et &agrave; <strong>h&eacute;berger</strong> votre site web dans un serveur local ou en ligne.</p>', 1, 2, '', '', '2018-07-18 18:36:17', 0),
(7, 'DéVELOPPEMENT D’APPLICATION (WINDEV, VISUAL STUDIO, …)', 'DAPP', 45, 115000, 'filière', '<p><em>Conception et r&eacute;alisation d&rsquo;une application de bureau dit desktop.</em></p><p><strong><em>Pr&eacute;requis</em></strong><em>&nbsp;: vocabulaire de d&eacute;veloppement d&rsquo;application &nbsp;de bureau (ex&eacute;cutable, code source, compilateur, &hellip;)</em></p>', 1, 2, '', '', '2018-07-18 18:36:17', 0),
(8, 'CONCEPTION ET RéALISATION DES BASES DE DONNéES RELATIONNELLES', 'BDDR', 60, 75000, 'filière', '<p><em>D&eacute;finition des concepts, pr&eacute;sentation des outils, conception et impl&eacute;mentation d&rsquo;une base de donn&eacute;es avec Ms Access, phpmyadmin, ...</em></p>', 1, 3, '', '', '2018-07-18 18:36:17', 0),
(9, 'LANGUE ANGLAISE', 'LANG', 50, 60000, 'filière', '<p><em>Vocabulary, grammar, </em></p>', 1, 2, '', '', '2018-07-18 18:36:17', 0),
(10, 'ALGORITHMES ET PROGRAMMATION (C, C++, JAVA, PASCAL,PYTHON)', 'ALGO', 60, 60000, 'filière', '<p><em>Pr&eacute;sentation des concepts de base, syntaxes, impl&eacute;mentation d&rsquo;un exemple de programme dans l&#39;un des langages sus-cit&eacute;s.</em></p>', 1, 2, '', '', '2018-07-18 18:36:17', 0),
(11, 'INITIATION à LA SAISIE ET AU TRAITEMENT DE TEXTE', 'SATT', 15, 15000, 'cours', '<p><em>Pr&eacute;sentation des concepts de la saisie, pr&eacute;sentation des outils du logiciel MS WORD, saisie, mise en forme, pagination, en t&ecirc;te et pieds de page&nbsp;;&hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 1),
(12, 'MISE EN FORME / FINALISATION DES MéMOIRES ET RAPPORTS ACADéMIQUES', 'FDOC', 15, 15000, 'cours', '<p><em>Pr&eacute;sentation des concepts de la saisie, pr&eacute;sentation des outils du logiciel MS WORD, saisie, mise en forme, pagination, en t&ecirc;te et pieds de page, sommaire automatique, section de page, liste de tableaux et figures automatique, r&eacute;f&eacute;rences automatique, note de bas de page&nbsp;;&hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(13, 'PROGRAMMATION EXCEL', 'EXCA', 24, 24000, 'cours', '<p><em>Formules Excel, insertion et manipulation des feuilles Excel, cr&eacute;ation des Graphismes, &hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(14, 'INTERNET ET APPLICATIONS', 'INAP', 15, 15000, 'cours', '<p><em>Pr&eacute;sentation de l&rsquo;Internet, pr&eacute;sentation des outils de navigation, pr&eacute;sentation des services et applications d&rsquo;Internet, manipulation de certains outils&hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(15, 'PRéSENTATION ASSISTéE PAR ORDINATEUR', 'PAOR', 15, 15000, 'cours', '<p><em>Pr&eacute;sentation du logiciel de Pr&eacute;sentation MS POWERPOINT&nbsp;; pr&eacute;sentation des concepts de la PAO avec MS POWERPOINT&nbsp;; manipulation du logiciel par un exemple&hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(16, 'CARTOGRAPHIE ASSISTéE PAR ORDINATEUR', 'CART', 21, 20000, 'cours', '<p><em>Pr&eacute;sentation des concepts de la cartographie, des outils de cartographie et manipulation avec cas d&rsquo;exemple&hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(17, 'MAINTENANCE INFORMATIQUE AVANCéE', 'MINA', 30, 35000, 'cours', '<p><em>Pr&eacute;sentation des composants de la machine, panne sur chacun d&rsquo;eux, moyen de pr&eacute;vention et si possible de r&eacute;paration&hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(18, 'DESSINS INDUSTRIELS', 'DIND', 24, 25000, 'cours', '<p><em>Pr&eacute;sentation et manipulation des outils de dessins techniques&nbsp;;&nbsp; cr&eacute;ation des plans de maison, d&rsquo;appartement&hellip;</em></p>', 1, 3, '', '', '2018-07-18 18:36:17', 0),
(19, 'Comptabilité Assistée PAR ORDINATEUR', 'CAOR', 30, 35000, 'cours', '<p><em>Pr&eacute;sentation et utilisation du logiciel <strong>SAGE SAARI </strong>dans les diff&eacute;rentes gestions</em></p>', 1, 2, '', '', '2018-07-18 18:36:17', 0),
(20, 'ANALYSE DE DONNéES AVEC SPSS', 'SPSS', 24, 25000, 'cours', '<p><em>Pr&eacute;sentation et manipulation du <strong>logiciel SPSS</strong> pour le traitement de donn&eacute;es&hellip;</em></p>', 1, 1, '', '', '2018-07-18 18:36:17', 26),
(21, 'TIC FOR BEGINNERS', 'TICF', 24, 15000, 'promotion', '<p>Son but est de faire d&eacute;couvrir &agrave; ces derniers l&#39;outil informatique dans ses diff&eacute;rentes utilisations. Elle a une dur&eacute;e de <strong>24H</strong> et est acc&eacute;e sur <strong>04</strong> modules:</p><ol><li><strong>La maintenance pour d&eacute;butant</strong> : donc l&#39;objectif est de faire d&eacute;couvrir &agrave; l&#39;&eacute;l&egrave;ve les composants de base de l&#39;ordinateur.</li><li><strong>Ms Word et Ms Excel par la pratique</strong> : l&#39;id&eacute;e de ce module est de faire d&eacute;couvrir &agrave; l&#39;apprenant l&#39;interface et la manipulation ces logiciels de la suite office.</li><li><strong>Les bases de donn&eacute;es avec Ms Access</strong> : Bien plus complexe que les modules sus-cit&eacute;s, ce module a pour but de faire d&eacute;couvrir &agrave; l&#39;&eacute;l&egrave;ve les concepts des bases de donn&eacute;es. il est sp&eacute;cialement destin&eacute; au &eacute;l&egrave;ves des niveaux Premi&egrave;re et Terminale.</li><li><strong>Internet et Mobiles</strong> : Avec l&#39;&eacute;volution exponentielle des TICs, nous avons pr&eacute;vu ce module pour faire d&eacute;couvrir &agrave; l&#39;apprenant le monde des TICs. La navigation sur internet, la cr&eacute;ation des comptes de messagerie, la partage de connexion entre un Ordinateur et un t&eacute;l&eacute;phone portable, certaines configurations r&eacute;seaux.</li></ol><p>La formation <strong>TIC For BEGINNERS</strong> est donc suffisament outill&eacute;e pour permettre &agrave; l&#39;&eacute;l&egrave;ve de mieux comprendre les concepts appris &agrave; l&#39;&eacute;cole et les pratiquer. Et de ce fait elle est dispens&eacute;e pendant les <strong>cong&eacute;s </strong>et <strong>vacances</strong>.&nbsp;</p>', 1, 1, '', '', '2018-07-18 18:36:17', 1),
(22, 'Apprentissage des outils de bureautique', 'AOBU', 30, 30000, 'cours', '<p>Cet enseignement h&eacute;rite directement de l&#39;enseignement <strong>S&eacute;cr&eacute;tariat Bureautique</strong>. L&#39;Apprentissage des outils Bureautique consiste prendre en main quelques un des logiciels de la Bureautique, en particulier ceux de la suite Office. L&#39;id&eacute;e ici est de prendre deux domaine de la bureautique (traitement de texte, tableurs, communication, archivages, Logiciel de PAO ou de Pr&eacute;AO etc...) et de les faire d&eacute;couvrir &agrave; l&#39;apprenant. Ce sera parfois le traitement de texte et les tableurs, Excel et la PAO, ... selon le choix de l&#39;apprenant.</p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(23, 'CREATION DE SITE WEB AVEC HTML ET CSS', 'HTML', 50, 60000, 'filière', '<p>Ce cours&nbsp; &eacute;t&eacute; con&ccedil;u pour permettre aux apprenantx de conna&icirc;tre les bases de la cr&eacute;ation des sites web avec html et css.</p><p>Certifi&eacute; d&#39;une attestation de formation, il se d&eacute;roule pendant une dur&eacute;e minimale de 50H. La formation passe par l&#39;introduction aux&nbsp;languages HTML et CSS jusqu&#39;&agrave; la cr&eacute;ation d&#39;un site web&nbsp;</p>', 1, 1, '', '', '2018-07-18 18:36:17', 0),
(24, 'Développement des sites et applications web avec PHP', 'DPHP', 60, 60000, 'filière', '<p>Concevez et r&eacute;alisez vos applications web et site web &agrave; l&#39;aide du langage PHP.</p><ul><li>par la m&eacute;thode de d&eacute;veloppement native</li><li>en utilisant le CMS Wordpress</li></ul>', 1, 1, '', '', '2018-07-18 18:36:17', 0);
