INSERT INTO `message` (`id`, `content`, `save_date`, `user`, `state`, `subject`, `mail`, `name`, `type`, `response`) VALUES
(1, '<p><strong>Le Centre de Formation Professionnelle MULTISOFT ACADEMY</strong> m&#39;a apport&eacute; beaucoup de bonnes choses par rapport &agrave; mes attentes dans la <strong>Saisie et le Traitement de Texte</strong>.&nbsp;Je suis tr&egrave;s fi&egrave;re de ma formation et j&#39;esp&egrave;re vivement m&#39;inscrire &agrave; un autre cours tr&egrave;s prochainement.</p><p>Apprenant &agrave; la Promotion <strong>17Z003, </strong><em>Initiation &agrave; la Saisie et au Traitement de Texte</em><strong>,&nbsp;</strong>Ann&eacute;e <strong>2017</strong></p>', '2017-11-21 12:05:33', 41, 1, '', '', '', 3, ''),
(2, '<p>&Agrave; mon arriv&eacute; au centre j&#39;ai &eacute;t&eacute; tr&egrave;s&nbsp; bien accueilli et encadr&eacute; par mes formateurs. <strong>Le centre joue honorablement le r&ocirc;le</strong> qui lui a &eacute;t&eacute; assign&eacute; par le <strong>MINEFOP </strong>et donne l&#39;iimpression de ne pas s&#39;arr&ecirc;ter en si bon chemin.</p><p>Apprenant &agrave; la Promotion <strong>17Z001, </strong><em>Apprentissage des Outils de Bureautique</em>,<strong>&nbsp;</strong>Ann&eacute;e <strong>2017</strong></p>', '2017-11-21 12:16:24', 39, 1, '', '', '', 3, ''),
(3, '<p>La formation f&ucirc;t parfaite car r&eacute;pondant &agrave; mes attentes. Toutes mes pr&eacute;ocupations que j&#39;avais rencontr&eacute;es ont eu une satisfaction. Le centre est socialement acccueillant et franchement, un seul mot pour r&eacute;sumer tout; c&#39;est <u><strong>G&Eacute;NIAL</strong></u></p><p>Aprennant &agrave; la promotion&nbsp;<strong>17Z010</strong>, <em>R&eacute;seaux Informatiques et C&acirc;blages</em>, Ann&eacute;e <strong>2017</strong></p>', '2017-12-19 11:35:15', 56, 1, '', '', '', 3, ''),
(4, '<p>Je trouve la formation satisfaisant, surtout pour le module que j&#39;ai choisi. J&#39;esp&egrave;re pouvoir me re-inscrire tr&egrave;s prochainement pour les autres modules afin de profiter davantages de l&#39;expertise du centre en informatique.</p><p>Apprenant &agrave; la promotion <strong>17Z010</strong>, <em>R&eacute;seaux Informatiques et C&acirc;blages</em>, Ann&eacute;e <strong>2017</strong></p>', '2017-12-19 11:38:18', 55, 1, '', '', '', 3, '');
INSERT INTO `msoft_flash` (`id`, `content`, `state`) VALUES
(1, '<p>Lancement des enseignements scientifiques &agrave; MULTISOFT ACADEMY</p><ul><li>Anglais scientifique;</li><li>Analyse de donn&eacute;es avec SPSS</li><li>Simultations avec MATLAB</li><li>R&eacute;daction de documents scientifiques avec LATEX</li></ul>', 0),
(2, '<p>R&eacute;sultats d&#39;&eacute;valuations de vacances d&eacute;j&agrave; disponibles au babillard.</p>', 0),
(3, '<p>Rentr&eacute;e acad&eacute;mique effective au centre de formation <strong>MULTISOFT ACADEMY</strong>;</p><p>d&eacute;j&agrave; <strong>trois inscriptions</strong> &agrave; nos enseignements.</p>', 0),
(4, '<p><strong>D&eacute;marrage d&#39;une nouvelle vague d&#39;enseignement dans le D&eacute;veloppement sites web et d&#39;applications</strong><br />Les inscriptions pour l&#39;apprentissage du d&eacute;veloppement d&#39;application ont d&eacute;j&agrave; commenc&eacute;...</p>', 0),
(5, '<p>R&eacute;sultats de formation au cours <strong>INITIATION &Agrave;&nbsp;SAISIE ET AU TRAITEMENT DE TEXTE</strong> : Les apprenants sont invit&eacute;s &agrave; recup&eacute;rer leurs&nbsp;certificats.</p>', 0),
(6, '<p>Les apprenants des vagues <strong>17Z001</strong> (<em>Apprentissage des outils de Bureautique</em>) et <strong>17Z003</strong> <em>(Initiation &agrave; la Saisie et au traitement de Texte</em>) sont invit&eacute;s &agrave; recup&eacute;rer leur Certificat de Fin de Formation le <strong>Mardi 14 Novembre 2017</strong> &agrave; partir de <strong>11H</strong> pr&eacute;cise.</p>', 0),
(7, '<p><strong>Evaluation &eacute;crites</strong> au centre &agrave; partir du mardi 12 D&eacute;cembre 2017. <strong>Bien vouloir consulter le babillard</strong>&nbsp;pour le programme des &eacute;valuations</p>', 0),
(8, '<p>R&eacute;sultats de Fin de formation de la vague <strong>17Z010&nbsp;</strong>MAINTENANCE INFORMATIQUE AVANC&Eacute;E disponible au barbillard.</p><p>Remise des attestations de formation Mercredi le <strong>03 Janvier 2017 &agrave; 11H</strong></p>', 0),
(9, '<p>Le Centre de Formation Professionnelle <strong>MULTISOFT ACADEMY</strong> &nbsp;informe &agrave; tous ses apprenants inscrits en son sein que le d&eacute;lai de paiement de la prochaine tranche des frais d&rsquo;inscription est fix&eacute;e le <strong>Vendredi 02 F&eacute;vrier 2018</strong>, d&eacute;lai de rigueur.</p><p><strong><em>NB&nbsp;: Les apprenants &agrave; la comptabilit&eacute; Informatis&eacute;e de Gestion inscrit depuis l&rsquo;ann&eacute;e pass&eacute;e sont tenus de respecter &eacute;galement ce d&eacute;lai pour la suite de leur cours interrompu. </em></strong></p><p>Par ailleurs il les informe &eacute;galement que les prochaines &eacute;valuations harmonis&eacute;es se tiendront du <strong>09 au 10 F&eacute;vrier 2018</strong>. Seuls les apprenants qui se seront acquitt&eacute;s de leur frais d&rsquo;inscription seront admis &agrave; composer ces &eacute;valuations.</p><p>La liste d&eacute;finitive des apprenants admis &agrave; composer sera affich&eacute;e le <strong>Lundi 05 F&eacute;vrier 2018.</strong></p>', 0),
(10, '<p>PROGRAMME DES EVALUATIONS HARMONIS&Eacute;ES DE F&Eacute;VRIER 2018 DISPONIBLE AU BARBILLARD</p><p>&nbsp;</p><p><strong><em>NB&nbsp;:</em></strong><em>&nbsp;<strong>Seuls les apprenants ayant pay&eacute; leur frais de formation selon l&rsquo;&eacute;ch&eacute;ancier affich&eacute; au babillard seront admis &agrave; composer. Les apprenants qui n&rsquo;auront pas respect&eacute; cet &eacute;ch&eacute;ancier mais qui payeront leur frais avant l&rsquo;&eacute;valuation se verront retirer deux(02) points de leur note d&rsquo;&eacute;valuation.</strong></em></p><p><strong><em>Bien vouloir se munir de vos quitus et re&ccedil;u pour v&eacute;rification.</em></strong></p>', 0),
(11, '<p>Le Centre de Formation Professionnelle <strong>MULTISOFT ACADEMY</strong> &nbsp;informe &agrave; tous ses apprenants inscrits en son sein que les prochaines &eacute;valuations harmonis&eacute;es se tiendront le <strong>04 Mars 2018</strong>. Seuls les apprenants qui se seront acquitt&eacute;s de leur frais d&rsquo;inscription seront admis &agrave; composer cette &eacute;valuation.</p><p>La liste d&eacute;finitive des apprenants admis &agrave; composer sera affich&eacute;e le <strong>vendredi 02 Mars 2018.</strong></p>', 0),
(12, '<p>&Eacute;valuations ce dimanche 4 mars 2018</p><p><strong>Vagues programm&eacute;es</strong> :</p><p>17Z006 SEBU Examen Finale</p><p>17Z007 ALGO TPA</p><p>17Z011 RICA CC2&nbsp;(note de TPI attendue)&nbsp;</p><p>17Z012 SEBU CC2</p><p>17Z013 SEBU CC2 (en attente de la note de TPI)&nbsp;</p><p>18Z016 SEBU CC1 (en attente de la note de TPI)&nbsp;</p><p>18Z017 SECO note de TPI uniquement</p>', 0),
(13, '<p>Toutes les vagues r&eacute;guli&egrave;rement inscrites en Comptabilite au Centre de Formation Multisoft Academy reprendrons les cours la semaine du <strong>16 avril.&nbsp;</strong><br />Par ailleurs la date limite de paiement des prochaines tranches de frais de formation est fix&eacute;e au vendredi <strong>21 Avril.</strong>&nbsp;</p><p>La Direction</p>', 1);
